public interface IProcessor
{
  public void process();
}
..
public class Engine
{
  private IProcessor processor;
  public IProcessor getProcessor()
  {
    return processor;
  }
  public void setProcessor(IProcessor processor)
  {
    this.processor = processor;
  }
}

section.p:
